import React from 'react'
import VehicleTable from './VehicleTable';
import audi from '../Assets/audi1.jpg';
import vwgolf from '../Assets/vwgolf1.jpg';
import toyota from '../Assets/toyoto1.jpg';
import passat from '../Assets/vwpassat1.jpg';
import mb from '../Assets/mb1.jpg';
import bmw from '../Assets/bmw1.jpg';
import Button from '@mui/material/Button';
import {useState} from 'react'


const VehicleModel = () => {

    let [imageChange, setImageChange] = useState(audi)
    let name = "Audi"
    let mark = "A1"
    let year = "2021"
    let doors = "4/5"
    let ac = "yes"
    let transmission = "Manual"
    let fuel = "Gasoline"


    return (
        <div style={{display:"flex", flexDirection:"Column"}}>
            <p style={{display:"flex", flexDirection:"Column"}}>
                <span style={{fontWeight:"bold", fontSize:"25px", fontFamily:"helvertica", marginLeft:"auto", marginRight:"auto"}}>Vehicle Models</span>
                <span style={{fontWeight:"bold", fontSize:"40px", fontFamily:"helvertica",marginLeft:"auto", marginRight:"auto"}}>Our rental fleet</span>
                <span style={{color:"grey", marginLeft:"auto", marginRight:"auto"}}>Choose from a variety of our amazing vehicles to rent for your next<br></br> 
                adventure or business trip</span>
            </p>
            <div style={{display:"flex",flexDirection:"row",  marginLeft:"auto", marginRight:"auto", marginTop:"5%"}}>
                <div style={{display:"flex",flexDirection:"column"}}>
                    <Button variant="contained" style={{margin:"2%"}} onClick={()=>setImageChange(audi)}>Audi A1 S-Line</Button>
                    <Button variant="contained" style={{margin:"2%"}} onClick={()=>setImageChange(vwgolf)}>VW Golf 6</Button>
                    <Button variant="contained" style={{margin:"2%"}} onClick={()=>setImageChange(toyota)}>Toyota Camry</Button>
                    <Button variant="contained" style={{margin:"2%"}} onClick={()=>setImageChange(bmw)}>BMW 320 ModernLine</Button>
                    <Button variant="contained" style={{margin:"2%"}} onClick={()=>setImageChange(mb)}>Mercedes Benz GLK</Button>
                    <Button variant="contained" style={{margin:"2%"}} onClick={()=>setImageChange(passat)}>VW Passat CC</Button>
                </div>
                <img src = {imageChange} alt='' style={{marginLeft:"10%", marginRight:"10%", width:"400px", height:"300px"}}></img>
                <VehicleTable style={{padding:"auto"}} name={name} mark = {mark} year = {year} doors = {doors} ac = {ac} transmission = {transmission} fuel = {fuel}/>
            </div>
        </div>
    )
}
export default VehicleModel;
